import './App.css';
import { Component } from 'react';
import { GoogleSearchBar } from './components/google-search/index.jsx';
import { RandomBackground } from './components/random-background/index.jsx';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      color: '#ffffff', // Initial color state
    };
  }

  handleColorChange = (newColor) => {
    this.setState({ color: newColor }); // Update state with new color
  };

  render() {
    const { color } = this.state; // Extract color from state

    return (
      <div className="App">
        <RandomBackground color={color} />
        <input
          type="color"
          value={color}
          onChange={(e) => this.handleColorChange(e.target.value)}
        />
        <h1>Ol√° 1</h1>
        <GoogleSearchBar></GoogleSearchBar>
      </div>
    );
  }
}
export default App;
